{
  "mcpServers": {
    "context7": {
      "command": "npx",
      "args": ["@context7/mcp-server"],
      "env": {
        "CONTEXT7_PROJECT_PATH": ".",
        "CONTEXT7_AI_PROVIDER": "claude"
      }
