// lib/output-generator.ts
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export interface OutputFormats {
  digitalPDF: string;
  individualImages: string[];
  printReadyFiles: string[];
  framedArt: string[];
}

export const generateAllFormats = async (
  storyData: StoryData,
  illustrations: string[]
): Promise<OutputFormats> => {
  // Generate PDF
  const pdf = new jsPDF('portrait', 'mm', 'a4');
  illustrations.forEach((img, index) => {
    if (index > 0) pdf.addPage();
    pdf.addImage(img, 'JPEG', 10, 10, 190, 140);
    pdf.text(storyData.pages[index].text, 10, 160);
  });

  // Generate individual high-res images
  const individualImages = await Promise.all(
    illustrations.map(async (img) => {
      const canvas = await html2canvas(document.createElement('div'));
      return canvas.toDataURL('image/png', 1.0);
    })
  );

  return {
    digitalPDF: pdf.output('datauristring'),
    individualImages,
    printReadyFiles: await generatePrintFiles(illustrations),
    framedArt: await generateFramedVersions(illustrations)
  };
};
