export const optimizeAIGeneration = async (
  mode: 'testing' | 'production',
  childAnalysis: ChildAnalysis
) => {
  const settings = COST_SETTINGS[mode];

  // Cache child analysis to avoid repeated API calls
  const cacheKey = `child_analysis_${hashChildFeatures(childAnalysis)}`;
  const cachedAnalysis = await getFromCache(cacheKey);

  if (cachedAnalysis && settings.cacheEnabled) {
    return cachedAnalysis;
  }

  const result = await generateStoryWithOptimization(settings);
  await setCache(cacheKey, result, '24h');

  return result;
};
